require 'set'

def find_words(board, words)
   trie = Trie.new
   max_path = 0

   words.each do |word|
       trie.add(word)
       max_path = word.size if word.size > max_path 
   end
 
   result = Set.new
  
   board.each.with_index do |row, i|
       row.each.with_index do |col, j|
           break if result.size == words.size
           next unless trie.starts_with?(col)
           dfs(board, i, j, result, "", trie, words.size, max_path)
       end
   end
    
   result.to_a
end

def dfs(board, row = 0, col = 0, result = [], path = "", trie, max_results, max_path)
    return if result.size >= max_results
    return if path.size > max_path
    
    value = board[row][col]
    return if value == "#"
    
    return unless trie.starts_with?(path)
    
    if trie.have?(path)
        result << path
        trie.delete(path)
    end
    
    adj = adjacent_coords(board, row, col)
            
    board[row][col] = "#"
    
    if trie.have?(path + value)
        result << path + value
        trie.delete(path + value)
    end
        
    adj.each do |x, y|
      dfs(board, x, y, result, path + value, trie, max_results, max_path)
    end
    
    board[row][col] = value
end

def adjacent_coords(board, row, col)
   rows = board.size
   cols = board.first.size  

   [        
        [row-1, col],
        [row, col+1],
        [row+1, col],
        [row, col-1]
    ].reject {|x, y| x.negative? || y.negative? || x >= rows || y >= cols }
end

class Trie
  def initialize()
    @tree = {}
    @values = {}
  end

  def add(key)
    key.chars.each_with_index.inject(tree) do |tree, (char, index)|
      tree[char] ||= {}
      tree[char][:terminal] = true if index == key.size - 1

      tree[char]
    end

    @values[key] = true 
    
    nil
  end

  def have?(key)
    return @values[key] if @values.key?(key)

    node = tree
    key.each_char do |char|      
      return false if node.nil? || node[char].nil?

      node = node[char]
    end

    node.key? :terminal
  end
    
  def starts_with?(word)
    return true if word == ""
      
    node = tree
    word.each_char do |char|
      if node.nil? || node[char].nil?
        return false
      end

      node = node[char]
    end
      
    true
  end
    
  def delete(key)  
    node = tree
    delete_recursive(key, node)

    @values[key] = false
      
    nil
  end

  private

  def delete_recursive(key, parent)
    return if key.nil? || key.empty? || parent.nil?

    head, tail = key[0], key[1..-1]

    child = parent[head] 
    delete_recursive(tail, child)

    if key.size == 1 && child.key?(:terminal)
      child.delete(:terminal)
    end
    
    if child.empty?
      parent.delete(head) # prune tree
    end
  end

  attr_reader :tree
end


require 'test/unit/assertions'
include Test::Unit::Assertions

def sample_case
  board = [["o","a","a","n"],["e","t","a","e"],["i","h","k","r"],["i","f","l","v"]]
  words = ["oath","pea","eat","rain"]
  expected = ["oath","eat"]

  assert_equal(find_words(board, words), expected)
end

